mapper.select:
SELECT distinct a.product_id, a.buyer, a.seller, a.content, a.send_date, a.type, b.product_name
FROM message a INNER JOIN product b
ON a.product_id = b.product_id
WHERE a.seller = #{seller}
AND a.buyer = #{buyer}
ORDER by a.send_date

SELECT a.product_id, a.buyer, a.seller, a.content, a.send_date, a.type, b.product_name
FROM message a INNER JOIN product b
ON a.product_id = b.product_id
WHERE a.buyer = #{buyer}
AND a.seller = #{seller}
ORDER by a.send_date
