mapper.update:
UPDATE product
SET product_like_cnt = product_like_cnt+1
WHERE product_id = #{prdtId}

UPDATE product
SET product_like_cnt = product_like_cnt-1
WHERE product_id = #{prdtId}

mapper.delete:
DELETE FROM product
WHERE account_id = #{accountId}
AND product_id = #{prdtId}

mapper.update:
UPDATE product
product_name = #{prdtName}, product_price = #{prdtPrice}, product_category = #{prdtCategory}, product_trade_loc = #{prdtTradeLoc}, product_condition = #{prdtCondition}, product_is_tradeable = #{prdtIsTradeable}, product_is_delivery_free = #{prdtIsDeliveryFree}, product_info = #{prdtInfo}, product_update_date = default
WHERE product_id = #{prdtId}
